function preload(){game.scale.scaleMode=Phaser.ScaleManager.SHOW_ALL,game.scale.pageAlignHorizontally=!0,game.scale.pageAlignVertically=!0,game.stage.backgroundColor="#eee",game.load.image("gamescene","assets/img/background.jpg")}function create(){game.physics.startSystem(Phaser.Physics.ARCADE),game.add.sprite(0,0,"assets/img/background.jpg")}function update(){}var game=new Phaser.Game(800,600,Phaser.AUTO,"",{preload:preload,create:create,update:update});